dunes-script-module.script:
var cat = Server.getConfigurationElementCategoryWithPath(path)
var configEls = cat.configurationElements
var values = new Array()

if(inputCategory != "" && inputCategory != null){
    for each(var c in configEls) {
        if(c.name == elementName){
            var attr =  c.getAttributeWithKey(inputCategory)
            values = attr.value
            break
        }

    }

}

//System.log(values)
return values
