dunes-script-module.script:
var cat = Server.getConfigurationElementCategoryWithPath("web-root")
var configEls = cat.configurationElements
var values = new Array()
for each(var c in configEls) {
    if(c.name == "requestFormValues"){
        var attr =  c.getAttributeWithKey(inputCategory)
        values = attr.value
        break
    }

}
//System.log(values)
return values
