dunes-script-module.script:
var tag = ""

if(vmClusterType != "" && vmClusterType != null){
    var institution = vmClusterType.split(" ")[0]
    //if(institution == "NHG")
     //   institution = "NNA"

    var type = vmClusterType.split(" ")[1]
    var env = ""

    if(vmClusterType.indexOf("Mission Critical") != -1){
        type = "CRI"
    }

    if(vmClusterType.indexOf("Standard") != -1){
        type = "STD"
    }

    if(environment == "P")
        env = "Prod"
    else if(environment == "R")
        env = "DR"
    else env = "NonProd"

    tag = "HDC-"+institution +"-"+type+"-"+env+"-Zone"
    System.log("VM Cluster type tag: "+tag)

}

 return tag
